scriptlet.script:
 var crlf = " \r\n";
 warningPing = "";
 reportPing = "";

 hostname = scriptletContext.get("host");
 output = scriptletContext.get("output");
 if (output == null)
 {
     reportPing += "Unable to ping server."+crlf;
     warningPing += "WARNING: Unable to ping server."+crlf;
 }
 else
 {
     pingLatency = scriptletRawResult['transmissionTimeMax'];
     pct = scriptletRawResult['percentagePacketsLost'];
     if (pingLatency == null || pingLatency == "")
     {
         reportPing += "Unable to obtain ping data."+crlf;
         warningPing += "WARNING: Unable to obtain ping data."+crlf;
     }
     else
     {
         reportPing += "Maximum latency pinging " + hostname + " was " + pingLatency + " ms."+crlf;
         if (pingLatency > 200) 
             warningPing += "WARNING: Ping took longer than 200ms."+crlf;
     }

     if (pct == null || pct == "")
     {
         reportPing += "Unable to determine packet loss."+crlf;
         warningPing += "WARNING: Unable to determine packet loss."+crlf;  

         scriptletResponse = "failure";
         scriptletResult = "Server not reachable"; 
     }
     else if (pct != 0) { 
         warningPing += "WARNING: Ping had " + pct + "% packet loss."+crlf;            
     }
}

scriptletContext.putGlobal("reportPing", reportPing);
scriptletContext.putGlobal("warningPing", warningPing);

   hostname = scriptletContext.get("host");
   var crlf = " \r\n";
   warningFS = "";
   reportFS = "";

   fileList = scriptletContext.get("ic_filesystem");

   if (fileList != null) {
      reportFS = "\n";
      // data is formatted in two columns: % mountname
      // the first row is a header
      // to split data from context it must be turned back into a native Javascript string
      // to do this we initialize a blank string and then append the variable
      fileData = "";
      fileList = ""+fileList;
      filedata = "";
      for (count = 0; count < fileList.length; count++){
	      var lastchar = fileData[fileData.length-1]
	       if (fileList[count] != '\n' && fileList[count] != '\r')
		       fileData += fileList[count];
	       else if (lastchar != '\n' && lastchar != 'r' && lastchar != ',')
		        fileData += ',';
      }
      lines = fileData.split(",");
      for (i = 3; i+2 < lines.length; i += 3) {
          if (lines[i] != null && lines[i + 1] != null && lines[i + 2] != null){
		      var left = parseInt(lines[i]);
		      var name = lines[i+1];
	 	      var total = parseInt(lines[i+2]);
              if (total > 0){
		          var percentage = new java.lang.Double(100 * (left/total)).doubleValue();
		          percentage = java.lang.Math.round(percentage * 100)/100;
           	      diskreport = "File System " + name + " has " + percentage + "% freespace left."+crlf;
                  if (percentage <= 10) {
                      warningFS += "WARNING: ";
                      warningFS += diskreport;
                  }
                  reportFS += diskreport;
	          }
         }
      }
   }

   scriptletContext.putGlobal("reportFS", reportFS);
   scriptletContext.putGlobal("warningFS", warningFS);

