reportMemory = "";

freeMemory = scriptletContext.get("ic_freeMemory");

if (freeMemory != null)
 freeMemory = freeMemory.split("\n")[1];
if (freeMemory != null) {
   reportMemory += "\nSystem has " + freeMemory + " kilobytes of free memory. "+crlf;
   if (freeMemory < 100000) warningMemory += "WARNING: System has less then 100MB of free memory. "+crlf;
}
else {
   reportMemory += "\nUnable to obtain memory statistics. "+crlf;
   warningMemory += "WARNING: Unable to obtain memory statistics. "+crlf;
}

scriptletContext.putGlobal("reportMemory", reportMemory);
scriptletContext.putGlobal("warningMemory", warningMemory);

   hostname = scriptletContext.get("host");
   var crlf = " \r\n";
   warningUptime = "";
   reportUptime = "";

   uptime = scriptletContext.get("ic_uptime");

   // add uptime info
   if (uptime != null && uptime != "") {
	  uptime = uptime.split("\n")[1];
      reportUptime += "\nUptime: "+uptime+" seconds."+crlf;

      var uptime_trimmed=uptime;
      mins = parseInt(uptime_trimmed/60);
      hrs = mins /60;
      days = hrs / 24;
      hours = hrs % 24;
      minutes = mins % 60;
      reportUptime += "System has been up for ";
      if (days > 0) reportUptime += java.lang.Math.floor(days).toString() + " day";
      if (days > 1) reportUptime += "s";
      if (hours > 0) reportUptime += ", " + java.lang.Math.floor(hours).toString() + " hour";
      if (hours > 1) reportUptime += "s";
      reportUptime += " " + minutes.toString() + " minute";
      if (minutes > 1) reportUptime += "s";
      reportUptime += "."+crlf;
      if (mins <= 15) warningUptime += "WARNING: System was rebooted in the last 15 minutes."+crlf;
   }
   else {
      reportUptime += "\nUnable to obtain uptime."+crlf;
      warningUptime += "WARNING: Unable to obtain uptime."+crlf;
   }

   scriptletContext.putGlobal("reportUptime", reportUptime);
   scriptletContext.putGlobal("warningUptime", warningUptime);

