warnings = ""
lines = data.split(/\n/);
for (i = 0; i < data.length; i++) {
     if (lines[i] == "" || lines[i] == null) continue;
     if (lines[i].match("No errors") != null) continue;

     warnings += "WARNING: " +lines[i] +crlf;
