var crlf = " \r\n";
warningProcesses = "";
reportProcesses = "";

//verify that key processes are running
necessary = processList = "";
processList += scriptletContext.get("ic_processList");
necessary += scriptletContext.get("vitalProcesses");
output = "";

if (processList == "")
{
        reportProcesses += "\nUnable to obtain information on vital processes.";
        warningProcesses += "WARNING: Unable to obtain information on vital processes.";
}
else
{
    //assume list is delimited by comma, pipe, space or newline
    processes = necessary.split(/[,\\\|\n\r]/);
    proc_output = "";
    count = 0;
    for (i = 0; i < processes.length; i++) 
    {
        if (processList.match(processes[i]) == null) 
        {
            if (processes[i] != null && processes[i] != "null" && processes[i] != "undefined")
            {
                count++;
                proc_output += "WARNING: " + processes[i] + " is not running. "+crlf;
            }
        }
    }

    // are any processes missing?
    if (proc_output == "") {
        reportProcesses += "\nAll vital processes are running. "+crlf;
    } 
    else if (count == processes.length)
    {
        reportProcesses += "\n"+proc_output;
        warningProcesses += "WARNING: Vital processes are not running.";
    }
    else
    {
        reportProcesses += "\n"+proc_output;
        warningProcesses += proc_output;
    }
}

scriptletContext.putGlobal("reportProcesses", reportProcesses);
scriptletContext.putGlobal("warningProcesses", warningProcesses);

hostname = scriptletContext.get("host");
var crlf = " \r\n";
warningIO = "";
reportIO = "";

iostat = scriptletContext.get("ic_iostat");

// deal with the iostat information
// this assume we are getting extended IO stat information and looks for anomalies
// there should be two readings, the first is the average since boot,
// the second one is the current 

output = "\r\nDevice IO checks:"+crlf;
var combined = "";

if (iostat != null && iostat != "")
{
    var split = iostat.split("\n\r, ");

    for (count = 0; count < split.length;count++)
    if (split[count] != null)
        combined += split[count] + "\n";
    if (combined != "")
    {
        output = "\r\nThe following devices had statuses that were not 'ok':"+crlf;
        output += combined;
    }
    else
        output += "All devices appear to be functioning normally."+crlf;
}
else
{
    output += "Error obtaining device information or device information not available."+crlf;
}

reportIO += output;

scriptletContext.putGlobal("reportIO", reportIO);
scriptletContext.putGlobal("warningIO", warningIO);

